<?phpuse yii\helpers\Html;use yii\widgets\DetailView;/* @var $this soft\web\View *//* @var $model common\modules\translation\models\SourceMessage */$this->title = $model->message;$this->params['breadcrumbs'][] = ['label' => Yii::t('app', 'Translations'), 'url' => ['index']];$this->params['breadcrumbs'][] = $this->title;?><?php if (!$this->isAjax): ?><?= Html::a(Yii::t('site', 'Update'), ['update', 'id' => $model->id], ['class' => 'btn btn-primary']) ?><?= Html::a(Yii::t('site', 'Delete'), ['delete', 'id' => $model->id], [    'class' => 'btn btn-danger',    'data' => [        'confirm' => Yii::t('app','delete-confirm-message'),        'method' => 'post',    ],]) ?><?php endif ?><?php$arr = [    'id',    'category',    'message:ntext',];foreach ($model->languages as $key => $one) {    $arr[] = ['label' => $key, 'value' => $one];}?><?= DetailView::widget([    'model' => $model,    'attributes' => $arr,]) ?>